package text;

public class ifinforloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int i;
       for(i=1;i<=10;i++) {
    	   if(i%2==0)
    		   System.out.println(i+" is even number");
    	   else
    		   System.out.println(i+" is odd number");
    	   
       }
	}

}
